import{_ as t,c as o,o as a,j as e,a as n}from"./chunks/framework.Dc8iq2K4.js";const Q=JSON.parse('{"title":"权力制衡","description":"","frontmatter":{},"headers":[],"relativePath":"introduction/governance/separation-of-power.md","filePath":"introduction/governance/separation-of-power.md","lastUpdated":null}'),r={name:"introduction/governance/separation-of-power.md"},s=e("h1",{id:"权力制衡",tabindex:"-1"},[n("权力制衡 "),e("a",{class:"header-anchor",href:"#权力制衡","aria-label":'Permalink to "权力制衡"'},"​")],-1),c=e("p",null,"Qubic 系统的核心设计理念为“去中心化”。其独特的治理体系旨在避免权力在单一机构中的集中。这一目标是通过将决策权明确分配给法定人数（Quorum）和仲裁者（Arbitrator）来实现的，确保了系统的运作更加公平和分散。",-1),i=e("p",null,"在这一定义下，法定人数是由 2/3+（或451+）个参与网络重要决策的计算者组成的集合体。这些决策涉及验证交易和保持共识——这指的是，在确认最终结果之前先要取得一致的意见。另一方面，仲裁者的角色则是解决争议、设定采矿算法的相关参数，并在每个纪元结束时公开计算者排名。同时，从表现不佳的计算者那里积累 QUs。",-1),d=e("p",null,"Qubic 生态系统中的两个实体都扮演着重要角色。所以防止一个实体同时控制法定人数和仲裁者是至关重要的，以维护网络的完整性和去中心化。如果同一实体控制了这两个，它可能会创造一种环境，在这种环境中，计算者会选择保持自己的排名而不是促进健康的竞争。这可能扰乱网络的整体平衡并阻碍其发展和进步。",-1),l=e("p",null,"简而言之，在 Qubic 体系中划分权力不仅仅是为了分散职责，更是为了守护核心的去中心化理念并保证网络的强大性、公平性和民主氛围。",-1),p=[s,c,i,d,l];function _(u,h,f,m,b,v){return a(),o("div",null,p)}const g=t(r,[["render",_]]);export{Q as __pageData,g as default};
